function extractData($url) {
    $result = get_remote_data($url);
    $contador = 1;
    $arrayfinelemento = array();
    $arrayauxiliar = array();
    $html = str_get_html($result);
    foreach ($html->find('.information') as $element) {
        foreach ($element->find("*") as $e) {
            if ($contador == 1 || $contador == 2) {
                $new_data = preg_replace("/[\t\s]+/", " ", trim($e->innertext));
                $data = preg_replace('/(<br>)+$/', '', $new_data);
                $data = preg_replace('/(")+$/', '', $data);

                if ($e->tag == "h2") {
                    $arrayauxiliar["titulo"] = $data;
                }
                if ($e->tag == "p" || $e->tag == "ul") {

                    $cuerpo = cleanStringd($e->outertext);

                    $arrayauxiliar["cuerpo"] = $cuerpo;
                }
            }

            if ($contador == 2) {

                $arrayfinelemento[] = $arrayauxiliar;
                $arrayauxiliar = [];
                $contador = 0;
            }
            $contador++;
        }
        return $arrayfinelemento;
    }
}

